mapper.select:
SELECT MEMNO,MEMID, MEMPASS, MEMNAME, MEMJOINDATE, MEMAUTHOR
	  FROM PJK_MEMBER 
	  WHERE memid = #{memId} 
	  		AND memPass = #{memPass} 
	  		AND MEMCHECKDEL = 'N'

SELECT MEMNO, MEMID, MEMPASS, MEMNAME, MEMJOINDATE, MEMAUTHOR
  FROM PJK_MEMBER
 WHERE MEMCHECKDEL = 'N' AND MEMID = #{memId}
